import React,{useEffect} from 'react';
import {useSelector} from "react-redux";
import Head from "next/head";
import {END} from "redux-saga";

import {Avatar,Card} from "antd";
import Applayout from "../components/AppLayout";
import wrapper from "../store/configureStore";
import {LOAD_USER_REQUEST} from "../reducers/userReducer";

function About(){
    const {userInfo} = useSelector(state=>state.userReducer);
    return (
        <>
            <AppLayout>
                <Head>
                    <title>About</title>
                </Head>  
                {userInfo
                ?(
                    <Card
                    actions={[
                        <div key="twit">
                            ss
                            <br />
                            {userInfo.Posts}
                        </div>,
                        <div key="following">
                            팔로잉
                            <br />
                            {userInfo.FOllowings}
                        </div>,
                        <div key="follower">
                            팔로워
                            <br />
                            {userInfo.Followings}
                        </div>
                    ]}>
                        <Card.Meta
                        avatar={<Avatar>{userInfo.nickname[0]}</Avatar>}
                        title={userInfo.nickname}
                        description="node bird"
                        />
                    </Card>
                )
                :null}   
            </AppLayout>
        </>
    )
}

export const getStatickProps = wrapper.getStaticProps(async (context)=>{
    context.store.dispatch({
        type:LOAD_USER_REQUEST,
        data:1,
    });
    context.store.dispatch(END); // request success 기다리기
    await context.store.sagaTask.toPromise(); // configureStore.js 에서 등록한 sgagTask 를 사용
})

export default About;
