import React,{useCallback,useState} from 'react';
import {PlusOutlined} from "@ant-design/icons"
function PostImages({images}){
    const [curImg,setcurImg] = useState(0);

    const onZoom = useCallback(()=>{
        setShowImagesZoom(curImg+1);
        console.log("click",curImg);
    },[])

    if(images.length === 1){
        return (
            <>
                <img role="presentation" src={images[0].src} alt={images[0].src} onClick={onZoom} />
            </>
        )
    }
    if(images.length === 2){
        return (
            <>
                <img style={{display:"inline-block",width:"50%"}} onClick={onZoom} src={images[0].src} alt=""/>
                <img style={{display:"inline-block",width:"50%"}} onClick={onZoom} src={images[1].src} alt=""/>
            </>
        )
    }

    return (
        <>
            <img style={{display:"inline-block",width:"50%"}} onClick={onZoom} src={images[0].src} alt=""/>
            <div
                role="presetation"
                style={{display:"inline-block",width:"50%",textAlign:"center",verticalAlign:"middle"}}
                onClick={onZoom}
            >
                <PlusOutlined />
                <br/>
                {images.length-1}의 사진 더보기
            </div>
        </>
    )
}

export default PostImages;
