import React,{useState,useCallback,useMemo,} from 'react';
import {Form,Input,Button} from "antd";
import Link from "next/link"
import styled from "styled-components"
import useInput from "../hooks/useInput"
import {useDispatch,useSelector} from "react-redux";
import {loginAction} from "../reducers/userReducer"

const ButtonWrapper = styled.div`
    margin-top:10px;
`

const StyleForm = styled(Form)`
    padding:10px;
`


function LoginForm({setIsLoggedIn}){

    const dispatch = useDispatch();
    const {isLoggingIn} = useSelector(state=>{console.log(state); return state;});
    const buttonStyle = useMemo(()=>({marginTop:10}),[]);

    const [id,onChangeId] = useInput("");
    const [password,onChangePassword] = useInput("");

    const onSubmitForm = useCallback(()=>{
        console.log(id,password);
        dispatch(loginAction({password,id}));
    },[id,password]);

    return (
        <StyleForm onFinish={onSubmitForm}>
            <div>
                <label htmlFor="use-id">아이디</label>
                <br/>
                <Input name="user-id" value={id} onChange={onChangeId} required />
            </div>
            <div>
                <label htmlFor="user-password">비번</label>
                <br/>
                <Input 
                    name="user-password"
                    value={password}
                    onChange={onChangePassword}
                    required
                />
            </div>
            <ButtonWrapper style={buttonStyle}>
                <Button type="primary" htmlType="submit" loading={false}>{isLoggingIn?"...":"로그인"}</Button>
                <Link href="/Signup"><a><button>회원가입</button></a></Link>
            </ButtonWrapper>
        </StyleForm>
    )
}

export default LoginForm;
