const initialState = {
    user:{
        isLoggedIn:false,
        user:null,
        signUpData:{},
        loginData:{},
    },
    post:{
        mainPosts:[]
    }
};

export const loginAction = data=>({
    type:"LOG_IN",
    data
});

const rootReducer = (state=initialState,action) =>{
    switch(action.type){
        case "LOG_IN":
            return {
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn:true,
                    user:action.data,
                }
            };
    }
}
