import React from 'react';
import {Button, Card,Popover,Avatar} from "antd"
import {RetweetOutlined,HeartOutlined,MessageOutlined,EllipsisOutlined} from "@ant-design/icons";
import {useSelector} from "react-redux";
import PostImages from "../components/PostImages"

function PostCard({post}){
    const [liked,setLiked] = useState(false);
    const [commentFormOpened,setCommentFormOpenend] = useState(false);
    const {me} = useSelector(state=>state.userReducer);
    const id = me?.id;//  me && me.id 
    return (
        <div>
            <Card 
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    <HeartOutlined key="heart" />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                        <Button.Group>
                            {(id && post.User.id === id) ? (
                            <>
                                <Button>수정</Button>
                                <Button type="danger">삭제</Button>
                            </>
                            ) :<Button>신고</Button> }
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>,
                ]}
                >
                    <Card.Meta 
                        avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                        title={post.User.nickname}
                        description={post.content}
                    />
                {/* <Image />
                <Content />
                <Button></Button> */}
            </Card>
            {/* <CommentForm />
            <Comments /> */}
        </div>
    )
}

export default PostCard;
