import React,{useCallback,useState,useRef} from 'react';
import {Form, Input,Button} from "antd"
import {useSelector,useDispatch} from "react-redux";
import {addPost} from "../reducers/postReducer"

function PostForm(){
    const dispatch = useDispatch();
    const imgInput = useRef();
    const {imagePaths} = useSelector(state=>state.postReducer);
    const [text,setText] = useState("");
    const onChangeText = useCallback((e)=>{
        setText(e.target.value);
    },[]);
    const onSubmit = useCallback(()=>{
        dispatch(addPost);
    },[])

    const onClickImgUpload = useCallback(()=>{
        imgInput.current.click();
    },[imgInput.current])

    return (
        <Form style={{margin:"10px 0 20px"}} onFinish={onSubmit}>
            <Input.TextArea 
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder="isnert your post"
            />
            <div>
                <input type="file" multiple hidden ref={imgInput}/>
                <Button onClick={onClickImgUpload}>img upload</Button>
                <Button type="primary" style={{float:"right"}} htmlType="submit">짹짹</Button> 
            </div>
            <div>
                {imagePaths.map((v)=>(
                    <div key={v} style={{display:"inline-block"}}>
                        <img src={v} style={{width:"200px"}} alt={v} />
                        <div>
                            <Button>remove</Button>
                        </div>
                    </div>   
                ))}
            </div>
        </Form>
    )
}

export default PostForm;
