import { Input } from 'antd';
import Form,{useCallback} from 'antd/lib/form/Form';
import React from 'react';
import useInput from "../hooks/useInput"
import {useSelector} from "react-redux"

function CommentForm({post}){
    const id = useSelector(state=>state.userReducer.me?.id);
    const [commentText,onChangeCommentText] = useInput("");
    const onSubmitComment = useCallback(()=>{
        console.log(id,commentText);
    },[commentText]);

    return (
        <Form onFinish={onSubmitComment}>
            <Form.item>
                <Input.TextArea value={commentText} onChange={onChangeCommentText}/>
                <Button type="primary" htmlType="submit">삐약</Button>
            </Form.item>
        </Form>
    )
}

export default CommentForm;
