import {HYDRATE} from "next-redux-wrapper";
const initialState = {
    user:{
        isLoggedIn:false,
        user:null,
        signUpData:{},
        loginData:{},
    },
    post:{
        mainPosts:[]
    }
};

const LOG_IN = "rootReducer/LOG_IN";
const LOG_OUT = "rootReducer/LOG_OUT";

export const logoutAction = ()=>({
    type:LOG_OUT
})

export const loginAction = data=>({
    type:LOG_IN,
    data
});

const rootReducer = (state=initialState,action) =>{
    switch(action.type){
        case LOG_IN:
            return {
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn:true,
                    user:action.data,
                }
            }
        case LOG_OUT:
            return {
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn:false,
                    user:null,
                }
            }
    }
}
