const initialState = {
    mainPosts:[{
        id:1,
        User:{
            id:1,
            nickname:"rsua",
        },
        content:"my first post",
        Images:[{
            src:""
        },
        {
            src:""
        },
        {
            src:""
        }],
        Comments:[{
            user:{
                nickname:"foxbox"
            },
            content:"wow good"
        },{
            user:{
                nickname:"rrr"
            },
            content:"holy shit"
        }],
    }],
    imagePaths:[],
    postAdded:false,
}

const ADD_POST = "postReducer/ADD_POST";

export const addPost = {
    type:ADD_POST
}

const dummyPost = {
    id:2,
    content:"this is dummy.",
    User:{
        id:1,
        nickname:"zero"
    },
    Images:[],
    Comments:[],
}

const postReducer = (state=initialState,action)=>{
    switch(action.type){
        case ADD_POST:
            return {
                ...state,
                mainPosts:[dummyPost,...state.mainPosts],
                postAdded:true,
            }
        default :
            return state;
    }
}

export default postReducer;
