import { CalculatorFilled, PushpinTwoTone } from "@ant-design/icons";
import {all,fork,takeEvery,put,delay,call} from "redux-saga/effects"
import {
    ADD_POST_REQUEST,ADD_POST_SUCCESS,ADD_POST_ERROR,
     ADD_COMMENT_REQUEST,ADD_COMMENT_SUCCESS,ADD_COMMENT_ERROR,
} from "../reducers/postReducer"

function* addPost(action){//({postContent})
    try{
        yield delay(1000);
        yield put({
            type:ADD_POST_SUCCESS,
            data:action.data
        });
    } catch(err){
        put({
            type:ADD_POST_ERROR,
            error:err,
        })
    }
}

function* addComment(action){//({postId,content,userId})
    try{
        // const result = yield call(addCommentAPI,action.data);
        yield delay(1000);
        yield put({
            type:ADD_COMMENT_SUCCESS,
            data:action
        })
    } catch(err){
        yield put({
            type:ADD_COMMENT_ERROR,
            data:err
        })
    }
}

function* watchAddPost(){
    yield takeEvery(ADD_POST_REQUEST,addPost);
}

function* watchAddComment(){
    yield takeEvery(ADD_COMMENT_REQUEST,addComment)
}

export default function* postSaga(){
    yield all([
        fork(watchAddPost),
        fork(watchAddComment),
    ])
}
