const initialState = {
    isLoggedIn:false,//login 여부
    isLoggingIn:false,//login ing
    isLoggingOut:false,//logout ing
    me:null,
    signUpData:{},
    loginData:{},
};


export const LOG_IN_REQUEST = "userReducer/LOG_IN_REQUEST";
export const LOG_IN_SUCCESS = "userReducer/LOG_IN_SUCCESS";
export const LOG_IN_ERROR = "userReducer/LOG_IN_ERROR";

export const LOG_OUT_REQUEST = "userReducer/LOG_OUT_REQUEST";
export const LOG_OUT_SUCCESS = "userReducer/LOG_OUT_SUCCESS";
export const LOG_OUT_ERROR = "userReducer/LOG_OUT_ERROR";


export const logoutRequestAction = ()=>({
    type:LOG_OUT_REQUEST
})

export const loginRequestAction = data=>({
    type:LOG_IN_REQUEST,
    data
});

const userReducer = (state=initialState,action)=>{
    switch(action.type){
        case LOG_IN_REQUEST:
            return {
                ...state,
                isLoggingIn:true,
            }
        case LOG_IN_SUCCESS:
            return {
                ...state,
                isLoggedIn:true,
                isLoggingIn:false,
                me:action.data
            }
        case LOG_IN_ERROR:
            return {
                ...state,
                isLoggingIn:false,
                isLoggedIn:false,
            }
        case LOG_OUT_REQUEST:
            return {
                ...state,
                isLoggingOut:true,
            }
        case LOG_OUT_SUCCESS:
            return {
                ...state,
                isLoggedIn:false,
                isLoggingOut:false,
                me:null,
            }
        case LOG_OUT_ERROR:
            return {
                ...state,
                isLoggingOut:false,
                isLoggedIn:false,
                me:null,
            }
        default :
            return state;
    }
}

export default userReducer;
